import React, { useState } from 'react';
import WelcomeScreen from './components/WelcomeScreen';
import TestPage from './components/TestPage';
import Results from './components/Results';
import { QUESTIONS, CATEGORIES } from './data/questions';

function App() {
  const [currentPage, setCurrentPage] = useState(0); // 0 = welcome, 1-6 = test pages, 7 = results
  const [answers, setAnswers] = useState({});

  const handleAnswer = (questionId, value) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: value
    }));
  };

  const handleStartTest = () => {
    setCurrentPage(1);
  };

  const handleNextPage = () => {
    if (currentPage < 6) {
      setCurrentPage(currentPage + 1);
    } else {
      setCurrentPage(7); // Go to results
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const calculateResults = () => {
    const results = {};

    // Initialize all categories with 0
    Object.values(CATEGORIES).forEach(category => {
      results[category] = 0;
    });

    // Sum up scores for each category
    QUESTIONS.forEach(question => {
      const answer = answers[question.id];
      if (answer !== undefined) {
        results[question.category] += answer;
      }
    });

    return results;
  };

  const getProgress = () => {
    if (currentPage === 0) return 0;
    if (currentPage === 7) return 100;
    return (currentPage / 6) * 100;
  };

  const isPageComplete = () => {
    if (currentPage === 0 || currentPage === 7) return true;

    const pageQuestions = QUESTIONS.filter(q => q.page === currentPage);
    return pageQuestions.every(q => answers[q.id] !== undefined);
  };

  if (currentPage === 0) {
    return <WelcomeScreen onStart={handleStartTest} />;
  }

  if (currentPage === 7) {
    const results = calculateResults();
    return <Results results={results} />;
  }

  return (
    <div className="container">
      <div className="card">
        <div className="progress-bar">
          <div className="progress-fill" style={{ width: `${getProgress()}%` }}></div>
        </div>

        <div className="page-info">
          Page {currentPage} of 6 | Questions {((currentPage - 1) * 9) + 1}-{currentPage * 9} of 54
        </div>

        <TestPage
          page={currentPage}
          answers={answers}
          onAnswer={handleAnswer}
        />

        <div className="navigation">
          <button
            className="btn btn-secondary"
            onClick={handlePrevPage}
            disabled={currentPage === 1}
          >
            Previous
          </button>

          <button
            className="btn btn-primary"
            onClick={handleNextPage}
            disabled={!isPageComplete()}
          >
            {currentPage === 6 ? 'See Results' : 'Next'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
